<?php/** * admin_index.php     ZCMS 配置写入 *  * @copyright    (C) 2005 - 2010  ZCMS * @licenes      http://www.zcms.cc * @lastmodify   2010-10-28 * @author       zhuayi   * @QQ			 2179942 */$pagename = '文章配置';//-------验证登录verify_admin('admin_username');//---写入文件$conent = '<?php'."\r\n";foreach ($_POST[$_POST['filename']] as $key=>$val){	$conent .= '$'.$key.' = "'.$val.'";'."\r\n"; }$conent .= '?>';if (!empty($_POST['url'])){	$_POST['request_url'] = '/article/index/';  //项目原始url，自定义url时使用}//---------写入SEO表seo('article',0);	write(ZCMS_ROOT.'/zcms/article/include/'.$_POST['filename'].'.php',$conent);//-------写入生成规则进入表if (!empty($_POST['article_config']['article_generate_path'])){	if ($_POST['article_config']['article_generate'] == 1)	$query->query("update ".T."article set article_generate_path = '".$_POST['article_config']['article_generate_path']."'");	else	{		$query->query("update ".T."article set article_generate_path = '' ");	}}//-------写入生成规则进入表if (!empty($_POST['article_config']['article_class_path'])){	if ($_POST['article_config']['article_generate'] == 1)	$query->query("update ".T."article_class set article_generate_path = '".$_POST['article_config']['article_class_path']."'");	else	{		$query->query("update ".T."article_class set article_generate_path = '' ");	}}//-------写入日志admin_log("article_config",'','',$pagename);showmsg('恭喜您,操作成功...',ret_cookie("backurl"));?>