<?php/** * admin_index.php     ZCMS 配置写入 *  * @copyright    (C) 2005 - 2010  ZCMS * @licenes      http://www.zcms.cc * @lastmodify   2010-10-28 * @author       zhuayi   * @QQ			 2179942 */$pagename = 'SOS程序配置';//---判断网站域名后边有没有"/"，如果有则替换掉if (substr($_POST['web_config']['weburl'],-1,1) == '/'){	$_POST['web_config']['weburl'] = substr($_POST['web_config']['weburl'],0,strlen($_POST['web_config']['weburl'])-1);}//---写入文件$conent = '<?php'."\r\n";foreach ($_POST[$_POST['filename']] as $key=>$val){	$conent .= '$'.$key.' = "'.$val.'";'."\r\n"; }$conent .= '?>';write(ZCMS_ROOT.'/data/include/'.$_POST['filename'].'.php',$conent);//-------写入日志admin_log("setup",'','','ZCMS程序配置');showmsg('恭喜您,操作成功...',ret_cookie("backurl"));?>